pragma solidity ^0.8.20;


/**
* @author @0xJonaseb11
* @title CertificationAuthority Contract
* @dev Acts like a Certificate Authority main house for enterprises
* @dev Only the Certifier can certify enterprises
* @dev Allows update of NFT certificate's metadata URI
* @dev Automatic revocation of certificates after expiry duration
*/
import {RolesManager} from "./RolesManager.sol";
import {Events} from "../utils/Events.sol";
import {DataTypes} from "../utils/DataTypes.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";

contract CertificationAuthority is RolesManager, ERC721URIStorage, ERC721Burnable {
    using DataTypes for DataTypes.Enterprise;
    using DataTypes for DataTypes.Certification;
    using DataTypes for DataTypes.InspectionReport;

    mapping(address => DataTypes.Certification) public certifications;
    mapping(uint256 => address) private _tokenToEnterprise;
    uint256 private _tokenIdCounter;

    constructor() ERC721("EnterpriseCertificate", "ECERT") {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(CERTIFIER_ROLE, msg.sender);
    }

    //////////////////////////////////////////////////////////
    ///////////// ENTERPRISE CERTIFICATION ///////////////////
    //////////////////////////////////////////////////////////

    /**
    * @dev This function is used to certify enterprises
    * @dev Only the Certifier can certify enterprises
    * @dev mints and assigns NFT certificate to an enterprise
    * @dev sets token URI to metadata URI of a certificate
    * @param enterpriseAddress The address of the enterprise to be certified
    * @param _industry The industry of the enterprise to be certified
    * @param _metadataURI The metadata URI of the certificate (IPFS hash)
    * @param validityDurationSeconds The validity duration of the certificate in seconds
    * @notice Emits CertificationGranted event on successful enterprise certification
    */

    function certifyEnterprise(
        address enterpriseAddress,
        string memory _industry,
        string memory _metadataURI,
        uint256 validityDurationSeconds
    ) external onlyValidAddress(enterpriseAddress) onlyRole(CERTIFIER_ROLE) {
        if (certifications[enterpriseAddress].isCertified) {
            revert CertificationAuthority__EnterpriseAlreadyCertified();
        }

        uint256 tokenId = _tokenIdCounter++;
        _safeMint(enterpriseAddress, tokenId);
        _setTokenURI(tokenId, _metadataURI);

        certifications[enterpriseAddress] = DataTypes.Certification({
            industry: _industry,
            metadataURI: _metadataURI,
            certifiedAt: block.timestamp,
            expiryDate: block.timestamp + validityDurationSeconds, // three years
            isCertified: true,
            tokenId: tokenId
        });

        _tokenToEnterprise[tokenId] = enterpriseAddress;

        emit Events.CertificationGranted(
            enterpriseAddress,
            _industry,
            _metadataURI,
            block.timestamp + validityDurationSeconds
        );
    }

    //////////////////////////////////////////////////////////
    ///////////// REVOCATION & EXPIRY HANDLING //////////////
    //////////////////////////////////////////////////////////


    /**
    * @dev allows the revocation of certifications from enterprises
    * @dev Only the `Certifier` or `Super Admin` can revoke certifications
    * @dev allows automatic destruction of NFT certificates via oppenzeppelin's`burn` function
    * @param enterpriseAddress The address of the enterprise to be revoked
    * @notice Emits CertificationRevoked event on successful enterprise revocation
    */
    function revokeCertification(address enterpriseAddress)
        external
        onlyValidAddress(enterpriseAddress)
        onlyRole(CERTIFIER_ROLE) onlyRole(DEFAULT_ADMIN_ROLE)
    {
        DataTypes.Certification storage cert = certifications[enterpriseAddress];
        if (!cert.isCertified) {
            revert CertificationAuthority__EnterpriseNotCertifiedYet();
        }

        if (cert.tokenId != 0) {
            burn(cert.tokenId); 
            delete _tokenToEnterprise[cert.tokenId];
        }

        cert.isCertified = false;
        emit Events.CertificationRevoked(enterpriseAddress);
    }


    /**
    * @dev allowes automatic revokation of NFT certificates after expiry duration 
    * @dev callable by anyone as long as expiry date kicks in
    * @notice Emits CertificationRevoked event on successful enterprise revocation
    */
    function revokeIfExpired(address enterpriseAddress) external {
        DataTypes.Certification storage cert = certifications[enterpriseAddress];

        if (!cert.isCertified) {
            revert CertificationAuthority__EnterpriseNotCertifiedYet();
        }
        if (block.timestamp < cert.expiryDate) {
            revert CertificationAuthority__CertificationNotExpiredYet();
        }

        if (cert.tokenId != 0) {
            burn(cert.tokenId);
            delete _tokenToEnterprise[cert.tokenId];
        }

        cert.isCertified = false;
        emit Events.CertificationRevoked(enterpriseAddress);
    }

    //////////////////////////////////////////////////////////
    ///////////// TRANSFER CONTROL ///////////////////////////
    //////////////////////////////////////////////////////////

    function _update(
        address to,
        uint256 tokenId,
        address auth
    ) internal override(ERC721) returns (address) {
        // Block all transfers except minting (to) and burning (address(0))
        if (auth != address(0)) {
            require(to == address(0), "Certificates are non-transferable");
        }
        return super._update(to, tokenId, auth);
    }

    //////////////////////////////////////////////////////////
    ///////////// VALIDATION & METADATA //////////////////////
    //////////////////////////////////////////////////////////

    function isCertificationValid(address enterpriseAddress)
        public
        view
        returns (bool)
    {
        DataTypes.Certification storage cert = certifications[enterpriseAddress];
        return cert.isCertified && block.timestamp <= cert.expiryDate;
    }

    function isCertificateValid(uint256 tokenId) public view returns (bool) {
        address enterprise = _tokenToEnterprise[tokenId];
        return isCertificationValid(enterprise);
    }


    /**
    * @dev updates the metadata URI of the NFT certificate
    * @dev Only the `Certifier` can update certification metadata URI
    * @param enterpriseAddress The address of the enterprise to be updated
    * @param newMetadataURI The new metadata URI of the certificate
    * @notice Emits CertificationUpdated event on successful metadata update
    */
    function updateCertificationMetadata(
        address enterpriseAddress,
        string calldata newMetadataURI
    ) external onlyValidAddress(enterpriseAddress) onlyRole(CERTIFIER_ROLE) {
        DataTypes.Certification storage cert = certifications[enterpriseAddress];
        if (!cert.isCertified) {
            revert CertificationAuthority__EnterpriseNotCertifiedYet();
        }

        if (cert.tokenId != 0) {
            _setTokenURI(cert.tokenId, newMetadataURI);
        }

        cert.metadataURI = newMetadataURI;
        emit Events.CertificationUpdated(enterpriseAddress, newMetadataURI);
    }

    //////////////////////////////////////////////////////////
    ///////////// GETTERS & OVERRIDES ////////////////////////
    //////////////////////////////////////////////////////////

    function getCertification(address enterpriseAddress)
        public
        view
        returns (DataTypes.Certification memory)
    {
        return certifications[enterpriseAddress];
    }

    function getEnterpriseByTokenId(uint256 tokenId)
        public
        view
        returns (address)
    {
        return _tokenToEnterprise[tokenId];
    }

    // ----- Required overrides for multiple inheritance --- //
    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721URIStorage, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
","keccak256":"0xbaf289d8ec21b4365ca13f74d42d32330ded1c3dff855d0769197a90afa9331a","license":"MIT"},"contracts/core/RolesManager.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;


/**
* @author @0xJonaseb11

* @dev manages the roles of the whole ecosystem 
* @dev Only Super Admin can assign roles
* @dev On very high roles, there's DAO voting
*/


import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { Events } from "../utils/Events.sol";
import { Errors } from "../utils/Errors.sol";
import { DataTypes } from "../utils/DataTypes.sol";


contract RolesManager is AccessControl, Errors {



    //////////////////////////////////////////////////////////
    // ==================================================== //
    /////// work on the default public role expiry period
    /////// Work on the logic of handling
    // ===================================================== //
    //////////////////////////////////////////////////////////



    bytes32 public constant CERTIFIER_ROLE = keccak256("CERTIFYING_BODY_ROLE");
    bytes32 public constant INSPECTOR_ROLE = keccak256("INSPECTION_MANAGER_ROLE");
    bytes32 public constant AUDITOR_ROLE = keccak256("AUDITOR_ROLE");
    bytes32 public constant ENTERPRISE_ROLE = keccak256("ENTERPRISE_ROLE");
    bytes32 public constant CERTIFICATE_FACTORY_ROLE = keccak256("CERTIFICATE_FACTORY_ROLE");
    bytes32 public constant PUBLIC_ROLE = keccak256("PUBLIC_ROLE");

    mapping(address => uint256) public publicRoleExpiry;

    uint256 public constant DEFAULT_PUBLIC_ROLE_DURATION = 2 minutes;

   // ----- modifiers
   modifier onlyValidAddress(address account) {
    if (account == address(0)) {
        revert InvalidAddress();
    }
    _;
   }


    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(CERTIFIER_ROLE,  msg.sender);
    }


    /** ----------- #claim temporary public role -------------- //
    * @dev allows users to claim temporary `PUBLIC` role for `durationInHours` hours on their visit to platform
    * @param durationInHours The duration in hours the user will have the `PUBLIC` role
    * @notice `durationInHours` must be less than 24 hours
    * @notice Emits PublicRoleGranted event on successful claim 
    */
    function claimTemporaryPublicRole(uint256 durationInHours) external {
        if (hasPublicRole(msg.sender)) {
            revert RolesManager__AlreadyHasRole();
        }
        if (durationInHours < 0 || durationInHours > 24) {
            revert RolesManager__InvalidDuration();
        }

        uint256 durationInSeconds = durationInHours * 1 hours;

        _grantRole(PUBLIC_ROLE, msg.sender);
        publicRoleExpiry[msg.sender] = block.timestamp + durationInSeconds;

        emit Events.PublicRoleGranted(msg.sender, durationInHours);
    }

    // ------- helper for client ---- //
    function getMaxPublicRoleDuration() public pure returns (uint256) {
        return 24;
    }


    /** --------- #  Auto-expiry check (Call periodically) ------- //
    * @dev helper to check expiry of `PUBLIC` role
    * @param account The account to check for role expiry
    * @notice Emits PublicRoleExpired event on expiry
    */
    function _hasRawPublicRole(address account) internal view returns (bool) {
        return hasRole(PUBLIC_ROLE, account);
    }
    function checkExpiryRoles(address account) public {
        if (!_hasRawPublicRole(account)) revert RolesManager__RoleDoesNotExist();
        if (publicRoleExpiry[account] < block.timestamp) {
            _revokeRole(PUBLIC_ROLE, account);

            emit Events.PublicRoleExpired(account);
        }
    }

    /** ---------- # Bulk Role Grant(Auto) ------- //
    * @dev Bulk grant roles to accounts
    * @param roles The array of roles to grant
    * @param accounts The array of accounts to grant roles
    * emits BulkRolesGranted event on successful grant
    */
    function bulkGrantRoles(
        bytes32[] calldata roles,
        address[] calldata accounts
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (roles.length == 0 || accounts.length == 0) {
            revert RolesManager__EmptyArray();
        }
        if (roles.length != accounts.length) {
            revert RolesManager__ArrayLengthMismatch();
        }

        for (uint256 i = 0; i < roles.length; i++) {
            _grantRole(roles[i], accounts[i]);
        }

        emit Events.BulkRolesGranted(roles, accounts);
    }

    /** -------- # Safe Role Transfer ------- //
    * @dev Transfers a role from one account to another
    * @param role The role to transfer
    * @param from The account to transfer the role from
    * @param to The account to transfer the role to
    * @notice Emits RoleTransferred event on successful transfer
    */
    function transferRole(
        bytes32 role,
        address from,
        address to  
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (!hasRole(role, from)) {
            revert RolesManager__RoleDoesNotExist();
        }
        _revokeRole(role, from);
        _grantRole(role, to);

        emit Events.RoleTransferred(role, from, to);
    }

    // ---- combined role check ----- //
    function getActiveRoles(address account) public view returns (
        bool isAdmin,
        bool isCertifier,
        bool isInspector,
        bool isAuditor,
        bool isEnterprise,
        bool isCertificateFactory,
        bool isPublic
    ) {
        return (
            hasRole(DEFAULT_ADMIN_ROLE, account),
            hasRole(CERTIFIER_ROLE, account),
            hasRole(INSPECTOR_ROLE, account),
            hasRole(AUDITOR_ROLE, account),
            hasRole(ENTERPRISE_ROLE, account),
            hasRole(CERTIFICATE_FACTORY_ROLE, account),
            hasRole(PUBLIC_ROLE, account)
        );
    }


    function _setupRole(bytes32 role, address account) internal virtual {
    _grantRole(role, account);
}


    ////////////////////////////////////////////////
    /////// MANAUAL ROLES MANAGEMENT  ////////////////
    ////////////////////////////////////////////////


    /////////////////////////////////
    ////// ROLE GRANTING ////////////
    /////////////////////////////////

    /** ---------# GRANTING ROLES ---------//
    * @dev Functions to grant roles to!
    * @param account The account to grant the role to
    * @notice Emits RoleGranted event on successful grant
    */
    function grantCertifier(address account) external onlyValidAddress(account) onlyRole(DEFAULT_ADMIN_ROLE) {
        grantRole(CERTIFIER_ROLE, account);

        emit Events.RoleGranted(CERTIFIER_ROLE, account);

    }

    function grantInspector(address account ) external onlyValidAddress(account) onlyRole(DEFAULT_ADMIN_ROLE){
        grantRole(INSPECTOR_ROLE, account);
        emit Events.RoleGranted(INSPECTOR_ROLE, account);
    }

    function grantAuditor(address account) external onlyValidAddress(account) onlyRole(DEFAULT_ADMIN_ROLE) {
        grantRole(AUDITOR_ROLE, account);
        emit Events.RoleGranted(AUDITOR_ROLE, account);
    }

    function grantEnterprise(address account) external onlyValidAddress(account) onlyRole(DEFAULT_ADMIN_ROLE){
        grantRole(ENTERPRISE_ROLE, account);
        emit Events.RoleGranted(ENTERPRISE_ROLE, account);
    }

    function grantCertificateFactory(address account) external onlyValidAddress(account) onlyRole(DEFAULT_ADMIN_ROLE) {
        grantRole(CERTIFICATE_FACTORY_ROLE, account);
        emit Events.RoleGranted(CERTIFICATE_FACTORY_ROLE, account);
    }

    function grantPublicRole(address account) external onlyValidAddress(account) onlyRole(DEFAULT_ADMIN_ROLE){
        grantRole(PUBLIC_ROLE, account);
        publicRoleExpiry[msg.sender] = block.timestamp + DEFAULT_PUBLIC_ROLE_DURATION;

        emit Events.RoleGranted(PUBLIC_ROLE, account);
    }


    /**
    * @dev Public role will be claimed instantly
    * On the entry of a user to our platform
    * Just clicking `claim public role` and user is assigned role
    * @notice I plan to make it a temporary role on every visit to our platform
    */

    /////////////////////////////////
    // ROLE REVOKATION /////////////
    ////////////////////////////////


    /** ------------- # REVOKING ROLES ------------- //
    * @dev Functions to revoke roles
    * @param account The account to revoke the role from
    * @notice Emits RoleRevoked event on successful revoke
    */
    function revokeCertifierRole(address account) external onlyValidAddress(account) onlyRole(DEFAULT_ADMIN_ROLE) {
        revokeRole(CERTIFIER_ROLE, account);
        emit Events.RoleRevoked(CERTIFIER_ROLE, account);
    }

    function revokeInspectorRole(address account) external onlyValidAddress(account) onlyRole(DEFAULT_ADMIN_ROLE){
        revokeRole(INSPECTOR_ROLE, account);
    }

    function revokeAuditorRole(address account) external onlyValidAddress(account) onlyRole(DEFAULT_ADMIN_ROLE){
    revokeRole(AUDITOR_ROLE, account);
    emit Events.RoleRevoked(AUDITOR_ROLE, account);
    }

    function revokeEnterpriseRole(address account) external onlyValidAddress(account) onlyRole(DEFAULT_ADMIN_ROLE){
        revokeRole(ENTERPRISE_ROLE, account);
        emit Events.RoleRevoked(ENTERPRISE_ROLE, account);
    }

    function revokeCertificateFactoryRole(address account) external onlyValidAddress(account) onlyRole(DEFAULT_ADMIN_ROLE) {
        revokeRole(CERTIFICATE_FACTORY_ROLE, account);
        emit Events.RoleRevoked(CERTIFICATE_FACTORY_ROLE, account);
    }

    function revokePublicRole(address account) external onlyValidAddress(account) onlyRole(DEFAULT_ADMIN_ROLE) {
        revokeRole(PUBLIC_ROLE, account);
        emit Events.RoleRevoked(PUBLIC_ROLE, account);
    }


    /////////////////////////////////////////////////
    //////// ROLE CHECKS //////////////////
    /////////////////////////////////////////////////

   /** --------- # CHECKING ROLES --------- //
   * @dev Functions to check if a certain account has the specified role
   * @param account The account to check 
   */
    function hasAdminRole(address account) public view returns(bool) {
        return hasRole(DEFAULT_ADMIN_ROLE, account);
    }

    function hasCertifierRole(address account) public view returns(bool) { 
        return hasRole(CERTIFIER_ROLE, account);
    }

    function hasInspectorRole(address account) public view  returns(bool) {
        return hasRole(INSPECTOR_ROLE, account);
    }

    function hasAuditorRole(address account) public view  returns(bool) {
        return hasRole(AUDITOR_ROLE, account);
    }

    function hasEnterpriseRole(address account) public view  returns(bool) {
        return hasRole(ENTERPRISE_ROLE, account);
    }

    function hasCertificateFactoryRole(address account) public view  returns(bool) {
        return hasRole(CERTIFICATE_FACTORY_ROLE, account);
    }

    function hasPublicRole(address account) public view returns(bool) {
        if (_hasRawPublicRole(account) && publicRoleExpiry[account] > block.timestamp) {
            return true;
        }
        return false;
    }
}","keccak256":"0xf4709898a14dd773491392570ec0a6cc413536b0c36b1c7ce8b3ddf5252a95c6","license":"MIT"},"contracts/modules/AuditorOversight.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { RolesManager } from "../core/RolesManager.sol";
import { CertificationAuthority } from "../core/CertificationAuthority.sol";
import { InspectionManager } from "./InspectionManager.sol";
import { Events } from "../utils/Events.sol";
import { DataTypes } from "../utils/DataTypes.sol";
import { Errors } from "../utils/Errors.sol";

contract AuditorOversight {
    RolesManager public immutable rolesManager;
    CertificationAuthority public immutable certAuthority;
    InspectionManager public immutable inspectionManager;

    // ------- #Audit cases ---- //
    mapping(uint256 => DataTypes.AuditCase) private _auditCases;
    uint256 private _caseCounter;

    // ------- #Case parameters ------ //
    uint256 public constant AUDIT_DEPOSIT = 0.1 ether;
    uint256 public constant APPEAL_WINDOW = 7 days;

    constructor (
        address _rolesManager,
        address _certAuthority,
        address _inspectionManager
    ) {
        rolesManager = RolesManager(_rolesManager);
        certAuthority = CertificationAuthority(_certAuthority);
        inspectionManager = InspectionManager(_inspectionManager);

    }

    modifier onlyAuditor() {
        if (!(rolesManager.hasAuditorRole(msg.sender))) {
            revert Errors.RolesManager__NotAuthorizedAuditor();
        }
        _;
    }

    modifier onlyEnterpriseOwner(uint256 caseId) {
        if (msg.sender != _auditCases[caseId].targetEnterprise) {
            revert Errors.AuditorOversight__NotAuthorizedEnterpriseOwner();
        }
        _;
    }

    //////////////////////////////////////////////////
    /////// AUDIT CASE MANAGEMENT ///////////////////
    ////////////////////////////////////////////////

    function initiateAudit(
        address _targetEnterprise,
        uint256 _inspectionId,
        string calldata _reason,
        string calldata _evidenceURI
    ) external payable {
        if (!(certAuthority.isCertificationValid(_targetEnterprise))) {
            revert Errors.CertificationAuthority__EnterpriseNotCertifiedYet();
        }

        _caseCounter++;
        _auditCases[_caseCounter] = DataTypes.AuditCase({
            id: _caseCounter,
            targetEnterprise: _targetEnterprise,
            inspectionId: _inspectionId,
            auditor: msg.sender,
            reason: _reason,
            evidenceURI: _evidenceURI,
            depositAmount: msg.value,
            status: DataTypes.AuditStatus.Pending,
            createdAt: block.timestamp,
            resolvedAt: 0
        });

        emit Events.AuditInitiated(
            _caseCounter,
            _targetEnterprise,
            _inspectionId,
            msg.sender,
            _reason,
            _evidenceURI
        );
    }

    ////////////////////////////////////////////
    ////////// AUDIT RESOLUTION  //////////////
    //////////////////////////////////////////
    function resolveAudit(
        uint256 caseId,
        bool upholdDecision,
        string calldata remarks
    ) external onlyAuditor {
        DataTypes.AuditCase storage auditCase = _auditCases[caseId];

        if (auditCase.status != DataTypes.AuditStatus.Pending) {
            revert Errors.AuditorOversight__CaseNotPending();
        }
            auditCase.status = upholdDecision 
                ? DataTypes.AuditStatus.Upheld
                : DataTypes.AuditStatus.Rejected;
            auditCase.resolvedAt = block.timestamp;

            // -- take action if audit is upheld
            if (upholdDecision) {
                certAuthority.revokeCertification(auditCase.targetEnterprise);
                inspectionManager.flagInspection(auditCase.targetEnterprise, auditCase.inspectionId, remarks);
            }

            // ------ return deposit to auditor
            payable(auditCase.auditor).transfer(auditCase.depositAmount);

            emit Events.AuditResolved(
                caseId,
                auditCase.targetEnterprise,
                msg.sender,
                upholdDecision,
                remarks
            );     
        }

        //////////////////////////////////////////////
        /////// ENTERPRISE APPEAL ///////////////////
        ////////////////////////////////////////////
        function fileAppeal(
            uint256 caseId,
            string calldata counterEvidenceURI
        ) external onlyEnterpriseOwner(caseId) {
            DataTypes.AuditCase storage auditCase = _auditCases[caseId];

            if (block.timestamp > auditCase.createdAt + APPEAL_WINDOW) {
                revert Errors.AuditorOversight__AppealWindowClosed();
            }

            auditCase.status = DataTypes.AuditStatus.Appealed;

            emit Events.AppealFiled(
                caseId,
                msg.sender,
                counterEvidenceURI,
                block.timestamp
            );
        }

        ////////////////////////////////////////////
        ////////// CROSS-CONTRACT INTEGRATION /////
        //////////////////////////////////////////


        /////////////////////////////////////////
        /////// COMPREHENSIVE GETTERS //////////
        ////////////////////////////////////////

        function getActiveAudits() public view returns(DataTypes.AuditCase[] memory) {
            uint256 activeCount;

            for (uint256 i = 0; i <= _caseCounter; i++) {
                if (_auditCases[i].status == DataTypes.AuditStatus.Pending) {
                    activeCount++;
                }
            }

            DataTypes.AuditCase[] memory activeAudits = new DataTypes.AuditCase[](activeCount);
            uint256 index;

            for (uint256 i = 1; i <= _caseCounter; i++) {
                if (_auditCases[i].status == DataTypes.AuditStatus.Pending) {
                    activeAudits[index++] = _auditCases[i];
                }
            }
            return activeAudits;
        }

    }
","keccak256":"0xdb73bbb514f85507b7276f67698323da1d322e4a6ed45eefab8a64a658ceb33f","license":"MIT"},"contracts/modules/InspectionManager.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
* @author @0xJonaseb11 
* @title InspectionManager Contract
* @dev Manages inspection process and reports
* @automates certification revocation when inspection is not passed
*/

///////////////////////////////////////////////////////////////////
/////// TO-DO : : Implement Inspection Validity specification/////
/////// Initial 30 days atleast - can be changed by Super Admin //
////// ASK REGIS HOW LONG A CERTIFICATION WOULD LAST ////////////
////////////////////////////////////////////////////////////////




//// -------- Fining enterprises ----- //
//// Implement enteprise fining and revocation // 
//// on chain --- fallabck contracts /// 


import { RolesManager } from "../core/RolesManager.sol";
import { CertificationAuthority } from "../core/CertificationAuthority.sol";
import { Events } from "../utils/Events.sol";
import { DataTypes } from "../utils/DataTypes.sol";

contract InspectionManager is RolesManager {
    RolesManager public immutable rolesManager;
    CertificationAuthority public immutable certAuthority;

    // Inspection relationships
    mapping(address => address[]) private _enterpriseInspectors;
    mapping(address => address[]) private _inspectorEnterprises;

    // Inspection reports storage
    mapping(address => DataTypes.InspectionReport[]) private _enterpriseReports;
    mapping(address => mapping(address => uint256)) private _lastInspectionTimestamps;

    // flagged inspection tracking
    mapping(uint256 => DataTypes.FlaggedInspection) private _flaggedInspections;
    mapping(address => uint256[]) private _enterpriseFlaggedReports;
    uint256 private _flagCounter;
    constructor(address _rolesManager, address _certAuthority) {
        rolesManager = RolesManager(_rolesManager);
        certAuthority = CertificationAuthority(_certAuthority);
    }



    ////////////////////////////////////////
    //////// ENHANCED ACCESS CONTROL //////
    //////////////////////////////////////

        modifier onlyCertifier() {
        if (!hasCertifierRole(msg.sender)) {
            revert RolesManager__NotAuthorizedCertifier();
        }
        _;
    }

    modifier onlyActiveInspector(address enterprise) {

        if (!hasInspectorRole(msg.sender)) {
            revert RolesManager__NotAuthorizedInspector();
        }

        if (!_isInspectorAssigned(enterprise, msg.sender)) {
            revert InspectionManager__NotAssignedToEnterprise();
        }

        if (block.timestamp > _lastInspectionTimestamps[enterprise][msg.sender]) {
            revert InspectionManager__InspectionPeriodExpired();
        }
        _;
    }



    ////////////////////////////////////////////
    ////////// INSPECTOR ASSIGNMENT ///////////
    ///////////////////////////////////////////
    function assignInspector(
        address enterprise,
        address inspector,
        uint256 validityPeriod
    ) external onlyCertifier {

        if (!certAuthority.isCertificationValid(enterprise)) {
            revert CertificationAuthority__EnterpriseNotCertifiedYet();
        }

        if (!hasInspectorRole(inspector)) {
            revert RolesManager__NotAuthorizedInspector();
        }

        _enterpriseInspectors[enterprise].push(inspector);
        _inspectorEnterprises[inspector].push(enterprise);
        _lastInspectionTimestamps[enterprise][inspector] = block.timestamp + validityPeriod;

        emit Events.InspectorAssigned(
            enterprise, inspector, validityPeriod
        );
    }

    ///////////////////////////////////////////////
    /////// INSPECTION REPORTING /////////////////
    /////////////////////////////////////////////
    function submitInspectionReport (
        address _enterprise,
        bool _passed,
        string calldata _remarks,
        string calldata _evidenceURI
    ) external onlyActiveInspector(_enterprise) {

        if (block.timestamp > _lastInspectionTimestamps[_enterprise][msg.sender]) {
            revert InspectionManager__InspectionPeriodExpired();
        }

        DataTypes.InspectionReport memory report = DataTypes.InspectionReport({
            inspector: msg.sender,
            enterprise: _enterprise,
            remarks: _remarks,
            evidenceURI: _evidenceURI, // IPFS hash
            inspectedAt: block.timestamp,
            passed: _passed,
            flagged: false
        });

        _enterpriseReports[_enterprise].push(report);

        // Auto-revoke certification if not passed
        if (!_passed) {
            certAuthority.revokeCertification(_enterprise);


        }



        emit Events.InspectionReportSubmitted(
            msg.sender,
            _enterprise,
            _passed,
            _remarks,
            _evidenceURI,
            block.timestamp
        );
    }


         /////////////////////////////////////////////////
        ////////// INSPECTION FLAGGING /////////////////
        ////////////////////////////////////////////////
        function flagInspection(
            address enterprise,
            uint256 reportIndex,
            string calldata reason
        ) external onlyRole(AUDITOR_ROLE) {
            if (reportIndex >_enterpriseReports[enterprise].length) {
                revert InspectionManager__InvalidReportIndex();
            }

            if (_enterpriseReports[enterprise][reportIndex].flagged) {
                revert InspectionManager__ReportAlreadyFlagged();
            }

            _enterpriseReports[enterprise][reportIndex].flagged = true;

            DataTypes.FlaggedInspection memory flagged = DataTypes.FlaggedInspection({
                enterprise: enterprise,
                reportIndex: reportIndex,
                report: _enterpriseReports[enterprise][reportIndex],
                flaggedBy: msg.sender,
                reason: reason,
                flaggedAt: block.timestamp
            });

            _flaggedInspections[_flagCounter] = flagged;
            _enterpriseFlaggedReports[enterprise].push(_flagCounter);
            _flagCounter++;

            emit Events.InspectionReportFlagged(
                enterprise,
                reportIndex,
                msg.sender,
                reason,
                block.timestamp
            );

        }


    ////////////////////////////////////////////////
    /////// CROSS-CONTRACT VALIDATION /////////////
    //////////////////////////////////////////////
    function _isInspectorAssigned(address enterprise, address inspector) private view returns(bool) {
        for (uint256 i = 0; i < _enterpriseInspectors[enterprise].length; i++) {
            if (_enterpriseInspectors[enterprise][i] == inspector) {
                return true;
            }
        }
        return false;
    }

    //////////////////////////////////////////////
    //////// COMPEREHENSIVE GETTERS /////////////
    ////////////////////////////////////////////
    function getEnterpriseInspectors(address enterprise)
    public 
    view 
    returns(address[] memory, uint256[] memory validityPeriods)
     {
        address[] memory inspectors = _enterpriseInspectors[enterprise];
        uint256[] memory periods = new uint256[](inspectors.length);

        for (uint256 i = 0; i < inspectors.length; i++) {
            periods[i] = _lastInspectionTimestamps[enterprise][inspectors[i]];
        }

        return (inspectors, periods);
     }

    function getInspectorReports(address inspector)
      public
      view 
      returns(DataTypes.InspectionReport[] memory) {

      address[] memory enterprises = _inspectorEnterprises[inspector];
      uint256 totalReports;

      // First pass: count reports
      for (uint256 i = 0; i < enterprises.length; i++) {
        totalReports += _enterpriseReports[enterprises[i]].length;
      }

     // Second pass: collect reports
    DataTypes.InspectionReport[] memory reports = new DataTypes.InspectionReport[](totalReports);
    uint256 counter;

    for (uint256 i = 0; i < enterprises.length; i++) {
        for (uint256 j = 0; j <_enterpriseReports[enterprises[i]].length; j++) {
            if (_enterpriseReports[enterprises[i]][j].inspector == inspector) {
                reports[counter++] = _enterpriseReports[enterprises[i]][j];
            }
        }
    }    

        return reports;
    }

    /////////////////////////////////////////
    /// Simple reports getter for testing////
    /////////////////////////////////////////
      function getEnterpriseInspectionReports(address enterprise) external view returns(DataTypes.InspectionReport[] memory) {

        if (_enterpriseReports[enterprise].length == 0) {
            revert InspectionManager__NoReportsAssociatedWithEnterprise();
        }
        return _enterpriseReports[enterprise];
    }



    //////////////////////////////////////////
    ///// FLAGGED INSPECTION GETTERS ////////
    ////////////////////////////////////////
    function getFlaggedInspections(uint256 limit, uint256 offset) 
        public view 
        returns(DataTypes.FlaggedInspection[] memory) {
            uint256 resultSize = limit > _flagCounter - offset ?  _flagCounter - offset : limit;
            DataTypes.FlaggedInspection[] memory result = new DataTypes.FlaggedInspection[](resultSize);

            for (uint256 i = 0; i < resultSize; i++) {
                result[i] = _flaggedInspections[offset + i];
            }

            return result;
    }

    function getEnterpriseFlaggedReports(address enterprise)
    public
    view
    returns(DataTypes.FlaggedInspection[] memory) {
        uint256[] memory flaggedIds = _enterpriseFlaggedReports[enterprise];
        DataTypes.FlaggedInspection[] memory result = new DataTypes.FlaggedInspection[](flaggedIds.length);

        for (uint256 i = 0; i < flaggedIds.length; i++) {
            result[i] = _flaggedInspections[flaggedIds[i]];
        }
        return result;
    }
}","keccak256":"0x3736c72f9bcd3c73321a2348209cb993e28a125686246b7ee72088ae0906e656","license":"MIT"},"contracts/utils/DataTypes.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
* @author @0xJonaseb11
* @dev This library contains the data types used by the contracts
* @dev These structs hold a huge role in maintaining contract states
*/
library DataTypes {
    struct Certification {
        string industry;
        string metadataURI;
        uint256 certifiedAt;
        uint256 expiryDate;
        bool isCertified;
        uint256 tokenId;
    }

    struct Enterprise {
        address enterpriseAddress;
        string name;
        string industry;
        string metadataURI;
        bool isRegistered;
        uint256 registrationDate;
        uint256 lastUpdated;
        uint256 certificateId;
    }

    struct InspectionReport {
        address inspector;
        address enterprise;
        string remarks;
        string evidenceURI; // IPFS hash
        uint256 inspectedAt;
        bool passed;
        bool flagged;
    }

    struct FlaggedInspection {
        address enterprise;
        uint256 reportIndex;
        InspectionReport report;
        address flaggedBy;
        string reason;
        uint256 flaggedAt;
    }

    struct NFTCertificate {
    address enterprise;
    address issuer;
    uint256 issuedAt;
    uint256 expiresAt;
    bool isRevoked;
    }

    struct AuditCase {
        uint256 id;
        address targetEnterprise;
        uint256 inspectionId;
        address auditor;
        string reason;
        string evidenceURI;
        uint256 depositAmount;
        AuditStatus status;
        uint256 createdAt;
        uint256 resolvedAt;
    }

    enum AuditStatus {
        Pending,
        Upheld,
        Rejected,
        Appealed
    }
}","keccak256":"0xdd54b6675ea527069420c989adb49b0cd9a67ba27876feee92e0ee8a5d99fa3e","license":"MIT"},"contracts/utils/Errors.sol":{"content":"// SPDX-License-Identifier: MIT
